import React,{useEffect,useState,useContext} from 'react'
import {UserContext} from '../../App'
import {useParams} from 'react-router-dom'

const Profile=()=>{
    const [userProfile,setProfile]=useState(null)
    
    const {state,dispatch}= useContext(UserContext)
    const {userid}= useParams()


    const [showfollow,setShowFollow]=useState(state?!state.following.includes(userid):true)
    
    useEffect(()=>{
fetch(`/user/${userid}`,{
    headers:{
        "Authorization":"Bearer "+localStorage.getItem("jwt")
    }
}).then(res=>res.json())
.then(result=>{
   //console.log(result)
  
   setProfile(result)
})
    },[])

const followUser=()=>{
    fetch('/follow',{
        method:"put",
        headers:{
            "Content-Type":"application/json",
            "Authorization":"Bearer "+localStorage.getItem("jwt")
        },
        body:JSON.stringify({
            followId:userid
        })
    }).then(res=>res.json())
    .then(data=>{
       
        dispatch({type:"UPDATE",payload:{following:data.following,followers:data.followers}})
        localStorage.setItem("user",JSON.stringify(data))
        setProfile((prevState)=>{
            return {
                ...prevState,
                user:{...prevState.user,
                    followers:[...prevState.user.followers,data._id]
                }
            }
        })
        setShowFollow(false)
    })
}



const unfollowUser=()=>{
    fetch('/unfollow',{
        method:"put",
        headers:{
            "Content-Type":"application/json",
            "Authorization":"Bearer "+localStorage.getItem("jwt")
        },
        body:JSON.stringify({
            unfollowId:userid
        })
    }).then(res=>res.json())
    .then(data=>{
       
        dispatch({type:"UPDATE",payload:{following:data.following,followers:data.followers}})
        localStorage.setItem("user",JSON.stringify(data))
        setProfile((prevState)=>{
            const newfollower=prevState.user.followers.filter(item=>item!=data._id)
                        return {
                ...prevState,
                user:{...prevState.user,
                    followers:newfollower
                }
            }
        })
       setShowFollow(true)
    })
}



    return (
        <>
        {userProfile ?
         <div style={{
            maxWidth:"550px",
            margin:"0px auto"
        }}>
             <div style={{
                 display:"felx",
                 justifyContent:"space-around",
                 margin:"18px 0px",
                 borderBottom:"1px solid grey"
             }}> 
                 <div>
                     <img style={{width:"160px",height:"160px",borderRadius:"80px"}}
                     src="https://images.unsplash.com/photo-1567351673954-0cb49f639dc4?ixid=MXwxMjA3fDB8MHxzZWFyY2h8MTM1fHxwZXJzb258ZW58MHwyfDB8&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60"
 
                     />
               
                   <div>
                       <h4>{userProfile.user.name}</h4>
                       <h5>{userProfile.user.email}</h5>
                       <div style={{display:"flex",justifyContent:"space-around",width:"108%"}}>
                           <h6>{userProfile.posts.length} posts </h6>
                           <h6>{userProfile.user.followers.length} followers </h6>
                           <h6>{userProfile.user.following.length} followings </h6>
                           <h6>40 followers</h6>
                           <h6>40 followings</h6>
                           </div>
                           {showfollow?  
                           <button className="btn waves-efect waves-light blue"
                           onClick={()=>followUser}
                           >Follow</button>
                           :
                            
                           <button className="btn waves-efect waves-light blue"
                           onClick={()=>unfollowUser}
                           >Unfollow</button>
                            }

                 </div>
                 </div>
             </div>
             <div className="gallery">{
                 userProfile.posts.map(item=>{
                     return (
                         <img key={item._id} className="item" src={item.photo}alt={item.title}/>
                     )
                 })
             }
                
                
             </div>
        </div>
       
        :<h2>Loading ...</h2>}
       </>
    )
}


export default Profile
