import React,{useEffect,useState,useContext} from 'react'
import {UserContext} from '../../App'
import {useParams} from 'react-router-dom'

const Profile=()=>{
    const [myProfile,setProfile]=useState(null)
    const {state,dispatch}= useContext(UserContext)
    const {userid}= useParams()
    console.log(userid)
    useEffect(()=>{
fetch(`/user/${userid}`,{
    headers:{
        "Authorization":"Bearer "+localStorage.getItem("jwt")
    }
}).then(res=>res.json())
.then(result=>{
   console.log(result)
   setProfile(result)
})
    },[])
    return (
       <div style={{
           maxWidth:"550px",
           margin:"0px auto"
       }}>
            <div style={{
                display:"felx",
                justifyContent:"space-around",
                margin:"18px 0px",
                borderBottom:"1px solid grey"
            }}> 
                <div>
                    <img style={{width:"160px",height:"160px",borderRadius:"80px"}}
                    src="https://images.unsplash.com/photo-1567351673954-0cb49f639dc4?ixid=MXwxMjA3fDB8MHxzZWFyY2h8MTM1fHxwZXJzb258ZW58MHwyfDB8&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60"

                    />
              
                  <div>
                      <h4>{userProfile.user.name}</h4>
                      <h5>{userProfile.user.email}</h5>
                      <div style={{display:"flex",justifyContent:"space-around",width:"108%"}}>
                          <h6>{userProfile.posts.length} posts </h6>
                          <h6>40 followers</h6>
                          <h6>40 followings</h6>
                          </div>  
                </div>
                </div>
            </div>
            <div className="gallery">{
                userProfile.posts.map(item=>{
                    return (
                        <img key={item._id} className="item" src={item.photo}alt={item.title}/>
                    )
                })
            }
               
               
            </div>
       </div>
    )
}


export default Profile
